<script>
import axios from "axios";
export default {
  name: "skillPage",
  data() {
    return {
      owner: "https://github.com/Allan-Sanchez",
      repost: [],
    };
  },
  mounted() {
    this.getData();
    console.log("here i have to call github user repos");
  },
  methods: {
    async getData() {
      let dataRepost = [];
      let dataOrder = [];
      let res = await axios.get(
        "https://api.github.com/users/Allan-Sanchez/repos"
      );
      res.data.forEach((item) => {
        let temp = {
          url: item.html_url,
          name: item.name,
          description: item.description,
          language: item.language,
          updated_at: item.updated_at,
        };
        dataRepost.push(temp);
      });

      dataOrder = await dataRepost.sort(
        (a, b) => new Date(b.updated_at) - new Date(a.updated_at)
      );

      for (let i = 0; i < 6; i++) {
        this.repost.push(dataOrder[i]);
      }
      // console.log(res.data)
    },
  },
};
</script>
